mapper.select:
	SELECT bbsno, title, grpno, indent, ansnum
    FROM bbs
    WHERE bbsno = #{bbsno}

	select count(refnum)
	from bbs
    where refnum = #{bbsno}

select count(bbsno) as cnt
from bbs
where bbsno = #{bbsno}
and passwd = #{passwd}

mapper.update:
update bbs
wname = #{wname}, title = #{title}, content = #{content},
filename = #{filename}, filesize = #{filesize}
where bbsno = #{bbsno}

update bbs
set viewcnt = viewcnt + 1
where bbsno = #{bbsno}

mapper.select:
	SELECT bbsno, wname, title, content,  viewcnt, wdate, filename
    FROM bbs

    	bbsno = #{bbsno}
